const realNumberArray = [4, 5.6, -9.8, 3.14, 42, 6, 8.34, -2];
const squareList = (arr) => {
  "use strict";
  // change code below this line
  const squaredIntegers = arr.filter(num => num > 0 && num % parseInt(num) === 0).map( (num) => Math.pow(num, 2) );
  // filter first takes in the num from array and checks if it is positive and if it can return a 0 remainder of the rounded integer version of itself. Then it takes the remaining positive integers as a base to exponent 2, and returns the result in a new array.

  // change code above this line
  return squaredIntegers;
};
// test your code
const squaredIntegers = squareList(realNumberArray);
console.log(squaredIntegers);
